import { StatusBar } from "expo-status-bar";
import { StyleSheet, Text, View } from "react-native";
import Login from "./Login/Login.js";
import React from "react";


export default function App() {
  //create a state to store user id
  const [userID, setUserID] = React.useState(null);
  const [pageID, setPageID] = React.useState("login");
  const [username, setUserName] = React.useState("");
  const [password, setPassword] = React.useState("");
  const setUserNameGlobal = (username) => {
    alert("username: " + username);
  }
  switch (pageID) {
    case "login":
      return <Login setUserID={setUserID} setPageID={setPageID} setUserNameGlobal={setUserGlobal} setPasswordGlobal={setPassword}/>;
    case "registration":
      return <Registration setPageID={setPageID} username={username} password={password} />;
    case "home":
      return <Text>Home</Text>;
    default:
      return <Login setUSerID={setUserID} setPageID={setPageID} />;
  }
}

